import axios from "axios";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function editarCliente() {
  const urlBase = "https://localhost:7018/api/Clientes/NuevoCliente";
  let navegacion = useNavigate();

  const {id} = use

  const [cliente, setCliente] = useState({
    nombre: "",
    direccion: "",
    telefono: "",
    email: "",
    nit: "",
  });

  const { nombre, direccion, telefono, email, nit } = cliente;

  const onInputChange = (e) => {
    setCliente({ ...cliente, [e.target.name]: e.target.value });
  };

  const onSubmit = async (e) => {
    e.preventDefault();

    await axios.post(urlBase, cliente);

    navegacion("/listaClientes");
  };

  return (
    <div className="container">
      <div className="container text-center" style={{ margin: "30px" }}>
        <h3>Editar Cliente</h3>
      </div>
      <form onSubmit={(e) => onSubmit(e)}>
        <div className="mb-3">
          <label htmlFor="nombre" className="form-label">
            Nombre
          </label>
          <input
            type="text"
            className="form-control"
            id="nombre"
            name="nombre"
            required={true}
            value={nombre}
            onChange={(e) => onInputChange(e)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="direccion" className="form-label">
            Direccion
          </label>
          <input
            type="text"
            className="form-control"
            id="direccion"
            name="direccion"
            required={true}
            value={direccion}
            onChange={(e) => onInputChange(e)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="telefono" className="form-label">
            Telefono
          </label>
          <input
            type="text"
            className="form-control"
            id="telefono"
            name="telefono"
            required={true}
            value={telefono}
            onChange={(e) => onInputChange(e)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="email" className="form-label">
            Email
          </label>
          <input
            type="email"
            className="form-control"
            id="email"
            name="email"
            value={email}
            onChange={(e) => onInputChange(e)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="nit" className="form-label">
            Nit
          </label>
          <input
            type="text"
            className="form-control"
            id="nit"
            name="nit"
            required={true}
            value={nit}
            onChange={(e) => onInputChange(e)}
          />
        </div>
        <div className="text-center">
          <button type="submit" className="btn btn-success me-3">
            Actualizar
          </button>
        </div>
      </form>
    </div>
  );
}
